import { useState } from 'react';
import { connect } from 'react-redux';
import dayjs from 'dayjs';
import { Button, Drawer, Space, Image, Divider, Typography } from 'antd';
import { FeaturedArticle } from '@/models/global';
import { RootState } from '@/models';

type StateProps = ReturnType<typeof mapState>;
interface Props extends StateProps {
  details?: FeaturedArticle;
  onClose: any;
  open: any;
}

const headingStyles: React.CSSProperties = {
  fontSize: '20px',
  textAlign: 'center',
};

const contentStyles: React.CSSProperties = {
  padding: '1em 0',
};

const { Title, Text, Paragraph } = Typography;

const mapState = (state: RootState) => ({
  selectedDate: state.global.selectedDate,
});

function DetailDrawer(props: Props) {
  return (
    <Drawer
      title={`Date: ${props.selectedDate.format('MMMM D')}, ${props.details
        ?.year}`}
      placement="bottom"
      headerStyle={headingStyles}
      bodyStyle={contentStyles}
      width={500}
      height="93%"
      onClose={props.onClose}
      open={props.open}
      mask={false}
      destroyOnClose={true}
      extra={
        <Space>
          <Button
            onClick={props.onClose}
            type="primary"
            className="cursor-pointer"
            danger
          >
            Close
          </Button>
        </Space>
      }
    >
      <div className="mx-auto max-w-7xl">
        <Title level={3} className="px-4 font-bold">
          {props.details?.text}
        </Title>
        <Title level={4} className="px-4">
          Pages
        </Title>
        {props.details &&
          props.details.pages.map((page) => (
            <div key={page.pageid}>
              <div className="mx-4 my-4 flex h-fit flex-col rounded-2xl border border-solid border-stone-300 shadow-lg sm:mt-8 sm:flex-row">
                <div className="h-fit w-full max-w-md p-0 sm:h-96 sm:w-2/5">
                  <Image
                    height="100%"
                    width="100%"
                    src={page.thumbnail?.source}
                    className="rounded-t-2xl sm:rounded-l-2xl sm:rounded-r-none"
                    preview={false}
                    fallback="data:image/png;base64,"
                  />
                </div>
                <div className="flex h-full w-full flex-col p-2 sm:w-3/5 sm:p-4">
                  <Title level={3} className="py-1 text-3xl font-bold">
                    {page.normalizedtitle}
                  </Title>
                  <Text className="py-1 font-semibold italic">
                    Updated: {dayjs(page.timestamp).format('DD MMM, YYYY')}
                  </Text>
                  <Divider className="m-0" />
                  <Paragraph className="max-h-16 py-1 font-semibold">
                    {page.description}
                  </Paragraph>
                  <Divider className="m-0" />
                  <Paragraph className="py-1">{page.extract}</Paragraph>
                </div>
              </div>
            </div>
          ))}
      </div>
    </Drawer>
  );
}

export default connect(mapState)(DetailDrawer);
