import { useState } from "react";
import dayjs from 'dayjs';
import { Button, Drawer, Space, Image, DrawerProps } from 'antd';
import { FeaturedArticle } from "@/models/global";


interface Props {
  details?: FeaturedArticle
  onClose: any
  open: any
}

const headingStyles: React.CSSProperties = {
  fontSize: '20px',
  textAlign: 'center',
};

const contentStyles: React.CSSProperties = {
  padding: 0,
};

function DetailDrawer(props: Props) {
  const [placement, setPlacement] = useState<DrawerProps['placement']>('bottom');

  return (
    <Drawer
      title={`On This Day ${props.details?.year}`}
      placement={placement}
      headerStyle={headingStyles}
      contentWrapperStyle={contentStyles}
      width={500}
      height="93%"
      onClose={props.onClose}
      open={props.open}
      extra={
        <Space>
          <Button onClick={props.onClose}>Close</Button>
        </Space>
      }
    >
      <div className="max-w-7xl mx-auto">
        <p className="p-4 font-bold">{props.details?.text}</p>
        {props.details && props.details.pages.map(page => (
          <div key={page.pageid}>
            <div className="flex flex-col sm:flex-row h-fit mx-4 mt-4 sm:mt-8">
              <div className="h-80 w-full sm:w-2/5 max-w-md">
                <Image
                  height="100%"
                  width="100%"
                  src={page.thumbnail?.source}
                  className='rounded-2xl'
                  preview={false}
                  fallback="data:image/png;base64,"
                />
              </div>
              <div className="flex flex-col h-full w-full sm:w-2/5 divide-y divide-dashed sm:px-4">
                <h1 className="text-3xl font-bold py-4">
                  {page.normalizedtitle}
                </h1>
                <p className="py-4">
                  Revised: {dayjs(page.timestamp).format("DD MMM YYYY")}
                </p>
                <div className="h-16 py-4">
                  <p>{page.description}</p>
                </div>
                <div className="py-4">
                  <p>{page.extract}</p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </Drawer>
  );
};

export default DetailDrawer;