import { useState } from 'react';
import { connect } from 'react-redux';
import Head from 'next/head';
import { Button, Carousel, Image, Typography, Divider } from 'antd';
import { Layout, Feed, DetailDrawer, Loading } from '@/components';
import { RootState } from '@/models';
import { FeaturedArticle } from '@/models/global';
import { CalendarOutlined } from '@ant-design/icons';

const mapState = (state: RootState) => ({
  featured: state.global.featured,
  isLoading: state.loading.effects.global.getFeaturedSelected,
});

type StateProps = ReturnType<typeof mapState>;
type Props = StateProps;

const { Title, Text } = Typography;

function Home(props: Props) {
  const [open, setOpen] = useState(false);
  const [details, setDetails] = useState<FeaturedArticle>();

  const showDetails = (selected: FeaturedArticle) => {
    const toView = props.featured.find(
      (item) => item.year === selected.year && item.text === selected.text
    );
    setDetails(toView);
    setOpen(true);
  };

  const onClose = () => {
    setOpen(false);
  };

  return (
    <Layout>
      <Head>
        <title>On This Day</title>
        <meta name="description" content="News and Historical Events." />
        <meta
          name="viewport"
          content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no"
        />
        <link rel="icon" href="/favicon.ico" />
      </Head>

      {props.isLoading && <Loading />}
      {!props.isLoading && (
        <div className="mx-auto flex min-h-screen w-full max-w-screen-2xl flex-col">
          <Carousel autoplay effect="fade" autoplaySpeed={7000} dots={false}>
            {props.featured &&
              props.featured.map((featured) => (
                <div key={featured.pages[0].pageid}>
                  <div className="mx-4 mt-4 flex h-fit flex-col sm:mt-8 sm:h-96 sm:flex-row">
                    <div className="h-96 w-full sm:w-2/5">
                      <Image
                        height="100%"
                        width="100%"
                        src={featured.pages[0].thumbnail?.source}
                        className="rounded-2xl"
                        preview={false}
                        fallback="data:image/png;base64,"
                      />
                    </div>
                    <div className="flex h-full w-full flex-col sm:w-3/5 sm:px-4">
                      <div className="flex flex-row content-center">
                        <CalendarOutlined
                          rev={undefined}
                          className="pr-2 text-lg"
                        />
                        <Title level={3} className="mt-3">
                          {featured.year}
                        </Title>
                      </div>
                      <Divider className="m-0" />
                      <div>
                        <Title level={4} className="line-clamp-2 max-h-16 py-2">
                          {featured.text}
                        </Title>
                      </div>
                      <Divider className="m-0" />
                      <div className="line-clamp-2 max-h-16 py-2">
                        <Text>
                          {featured.pages[0].description
                            ? featured.pages[0].description
                            : ''}
                        </Text>
                      </div>
                      <Divider className="m-0" />
                      <div className="py-4">
                        <Text className="line-clamp-3 sm:line-clamp-5">
                          {featured.pages[0].extract}
                        </Text>
                      </div>
                      <Button
                        className="my-2 ml-auto mr-2 w-fit"
                        type="primary"
                        onClick={() => showDetails(featured)}
                      >
                        Read More
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
          </Carousel>
          <Feed heading="Highlights" featured={props.featured} />
          <DetailDrawer details={details} onClose={onClose} open={open} />
        </div>
      )}
    </Layout>
  );
}

export default connect(mapState)(Home);
